mapper.select:
select post_id, title, content_md, content_html, created_date, modified_date, m.member_id, username, picture from post p join member m on p.member_id = m.member_id
where title like '%${keyword}%' or content_md like '%${keyword}%'
order by created_date desc limit #{startIndex}, #{pageSize}

mapper.update:
update post
set title = #{title},
    content_md = #{contentMd},
    content_html = #{contentHtml},
    modified_date = #{modifiedDate}
where post_id = #{postId}
